package basicofjava;

public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=20;
		int b=12;
		int c=6;
		
		if(a>b && b>c)
			System.out.println("a is greater");
		else {
			System.out.println("c is greater");
		}
		if(b>c && b>a)
		{
			System.out.println("b is greater");
		}

	}

}
